DROP VIEW "public"."search_view";--> statement-breakpoint
CREATE VIEW "public"."search_view" AS (((((select text 'card' as "type", "card_id", "card_code", "fullTextSearch" from "cards") union all (select text 'pack' as "type", "pack_id", "pack_code", "fullTextSearch" from "packs")) union all (select text 'campaign' as "type", "campaign_id", "campaign_code", "fullTextSearch" from "campaigns")) union all (select text 'scenario' as "type", "scenario_id", "scenario_code", "fullTextSearch" from "scenarios")) union all (select text 'encounter_set' as "type", "encounter_id", "encounter_code", "fullTextSearch" from "encounter_sets"));