DROP VIEW "public"."search_view";--> statement-breakpoint
CREATE VIEW "public"."search_view" AS (((((select text 'card' as "type", "card_id", "card_code", "card_name", "fullTextSearch" from "cards") union all (select text 'pack' as "type", "pack_id", "pack_code", "pack_name", "fullTextSearch" from "packs")) union all (select text 'campaign' as "type", "campaign_id", "campaign_code", "campaign_name", "fullTextSearch" from "campaigns")) union all (select text 'scenario' as "type", "scenario_id", "scenario_code", "scenario_name", "fullTextSearch" from "scenarios")) union all (select text 'encounter_set' as "type", "encounter_id", "encounter_code", "encounter_name", "fullTextSearch" from "encounter_sets"));